import React, { useContext } from 'react'
import './../App.css'
import { myContext } from '../Context'

function MovieName() {
  const {movieTitle} = useContext(myContext)
  let movie = Array.from(movieTitle.toUpperCase())
  let vowels = ['A','E','I','O','U']
  console.log(movie)

  let array =[]
   movie.map((v,i)=>{
    // console.log(v)

    if(vowels.includes(v)){
      console.log(v ,'vowels')
      array[i] = v
    }
    else if(v === ' ' ){
      array[i] = ' '
       
    }
    else{
      array
    }

  

  })
  console.log(array)

  return (
    <>
    <div className="movieName">
        <ul className='displayUL'>
            {array.map((v,i)=>{
              return(
                <li>{v}</li>
              )
            })}
        </ul>
    </div>
    </>
  )
}

export default MovieName
