import React, { useState } from 'react'
import { myContext } from './Context'
import Movie from './data'

function ContextProvider(props) {
// STATE 1 to render play button
 const [isButton , setIsButton] = useState(false)
//  STATE 2 to render game popup
 const [gamePopup,setGamePopup] = useState(false)
// STATE 3 to render hint popup
const [ hintPopup , setHintPopup] = useState(false)
 
// FUNCTION 1 
 const playGame = ()=>{
    setGamePopup(true)
    setIsButton(false)
    
 }
     
  return (
    <myContext.Provider value={{playGame,isButton,setIsButton,gamePopup,hintPopup,setHintPopup}}>
        {props.children}
    </myContext.Provider>
    
  )
}

export default ContextProvider
